<?php/** * @package  bulkPlugin */namespace Inc\Api\Callbacks;class BulkScheduleCallbacks{  public function scheduleSectionManager()  {  }  public function scheduleSanitize($input)  {    return $input;  }  public function checkboxField( $args )  {    $name = $args['label_for'];    $classes = $args['class'];    $option_name = $args['option_name'];    $checkbox = get_option( $option_name );    $checked = isset($checkbox[$name]) ? ($checkbox[$name] ? true : false) : false;    echo '<div class="' . $classes . '"><input type="checkbox" id="' . $name . '" name="' . $option_name . '[' . $name . ']" value="1" class="" ' . ( $checked ? 'checked' : '') . '><label for="' . $name . '"><div></div></label></div>';  }  public function textField($args)  {    $name = $args['label_for'];    $option_name = $args['option_name'];    $value = '';    $input = get_option($option_name);    if (isset($input) && !empty($input)) {      $value = $input;    } else {      $value = '';    }    echo '<div class="form-group"><input type="text" class="regular-text form-control" id="' . $name . '" name="' . $option_name . '" value="' . $value . '" placeholder="' . $args['placeholder'] . '"></div>';  }  public function selectPostType($args)  {    $postTypes = get_post_types();    $excludedPostTypes = array(      'revision',      'nav_menu_item',      'vc_grid_item',      'page',      'attachment',      'custom_css',      'customize_changeset',      'oembed_cache',      'user_request',      'vc4_templates',      'emailtemplate',      'wp_block',    );    foreach ($excludedPostTypes as $key) {      $keyToDelete = array_search($key, $postTypes);      unset($postTypes[$keyToDelete]);    }    $name = $args['label_for'];    $option_name = $args['option_name'];    $value = '';    $input = get_option($option_name);    if (isset($input) && !empty($input)) {      $value = $input;    } else {      $value = '';    }    ?>    <select name="<?php echo $name; ?>" id="cat_post" class="selectpicker" data-live-search="true">      <option value="">Select Post Type</option>      <?php foreach ($postTypes as $key => $posttype):        $selected = $value == $key ? 'selected' : '';        ?>        <option value="<?php echo $key; ?>" <?php echo $selected; ?>><?php echo ucfirst($key) ?></option>      <?php endforeach ?>    </select>    <?php  }  public function selectPostCat($args)  {    $categories = get_categories(      array(        'hide_empty'      => false,      )    );    $name = $args['label_for'];    $option_name = $args['option_name'];    $value = '';    $input = get_option($option_name);    if (isset($input) && !empty($input)) {      $value = $input;    } else {      $value = '';    }    ?>    <select name="<?php echo $name; ?>" id="cat_post" class="selectpicker" data-live-search="true">      <option value="">Select Category</option>      <?php foreach ($categories as $key => $category):        $selected = $value == $category->term_id ? 'selected' : '';        ?>        <option value="<?php echo $category->term_id; ?>" <?php echo $selected; ?>><?php echo ucfirst($category->name) ?></option>      <?php endforeach ?>    </select>    <?php  }  public function selectPostUnCat($args)  {    $name = $args['label_for'];    $option_name = $args['option_name'];    $value = '';    $input = get_option($option_name);    if (isset($input) && !empty($input)) {      $value = $input;    } else {      $value = '';    }    $selected = $value == 1 ? 'selected' : '';    ?>    <select name="<?php echo $name; ?>" id="cat_post" class="selectpicker" data-live-search="true">      <option value="">Select Category</option>        <option value="1" <?php echo $selected; ?>><?php echo ucfirst('Uncategorized') ?></option>    </select>    <?php  }  public function radioField($args)  {    $name = $args['label_for'];    $option_name = $args['option_name'];    $value = '';    $input = get_option($option_name);    if (isset($input) && !empty($input)) {      $value = $input;    } else {      $value = '';    }    $checked_false = $input == 'false' ? 'checked' : '';    $checked_true = $input == 'true' ? 'checked' : '';    echo '<div class="form-group"><input type="radio" class="schedule-delete" id="delete_now_check" name="' . $option_name . '" value="false" '.$checked_false.'> <label for="delete_now_check">Delete Now</label> <input type="radio" class="schedule-delete" id="schedule_check" name="' . $option_name . '" value="true" '.$checked_true.'> <label for="schedule_check">Schedule</label></div>';  }}