<?php/** * @package  bulkPlugin */namespace Inc\Base;use Inc\Api\Callbacks\BulkScheduleCallbacks;use Inc\Api\SettingsApi;use Inc\Base\Controller;use Inc\Api\Callbacks\BulkAdminCallbacks;use Inc\Api\Callbacks\BulkManageCallbacks;class ScheduleController extends Controller{  public $settings;  public $callbacks;  public $callbacks_mngr;  public $pages = array();  public function register()  {    if (!$this->activated_bulk_option('bulk_manager')) return;    $this->settings = new SettingsApi();    $this->callbacks = new BulkAdminCallbacks();    $this->schedule_callbacks = new BulkScheduleCallbacks();    $this->setSubpages();    $this->setSettings();    $this->setSections();    $this->setFields();    $this->settings->addSubPages($this->subpages)->register();  }  public function setSubpages()  {    $this->subpages = array(      array(        'parent_slug' => 'bulk_delete_plugin',        'page_title' => 'Schedule Delete',        'menu_title' => 'Schedule Delete',        'capability' => 'manage_options',        'menu_slug' => 'bulk_schedule_options',        'callback' => array($this->callbacks, 'scheduleDashboard')      )    );  }  public function setSettings()  {    $args = array(      array(        'option_group' => 'bulk_schedule_plugin_form_settings',        'option_name' => 'select_cat_post_type',        'callback' => array($this->schedule_callbacks, 'scheduleSanitize')      ),      array(        'option_group' => 'bulk_schedule_plugin_form_settings',        'option_name' => 'select_post_cat',      ),      array(        'option_group' => 'bulk_schedule_plugin_form_settings',        'option_name' => 'delete_now_schedule',      ),      array(        'option_group' => 'bulk_schedule_plugin_form_settings',        'option_name' => 'schedule_time',      ),      array(        'option_group' => 'bulk_schedule_plugin_form_settings',        'option_name' => 'replace_text',      ),    );    $this->settings->setSettings($args);  }  public function setSections()  {    $args = array(      array(        'id' => 'bulk_schedule_options_index',        'title' => '',        'callback' => '',        'page' => 'bulk_schedule_options'      )    );    $this->settings->setSections($args);  }  public function setFields()  {    $args = array(      array(        'id' => 'select_cat_post_type',        'title' => 'Select Post',        'callback' => array($this->schedule_callbacks, 'selectPostType'),        'page' => 'bulk_schedule_options',        'section' => 'bulk_schedule_options_index',        'args' => array(          'option_name' => 'select_cat_post_type',          'label_for' => 'select_cat_post_type',        )      ),      array(        'id' => 'select_post_cat',        'title' => 'Select Category',        'callback' => array($this->schedule_callbacks, 'selectPostUnCat'),        'page' => 'bulk_schedule_options',        'section' => 'bulk_schedule_options_index',        'args' => array(          'option_name' => 'select_post_cat',          'label_for' => 'select_post_cat',        )      ),      array(        'id' => 'delete_now_schedule',        'title' => 'Delete Type',        'callback' => array($this->schedule_callbacks, 'radioField'),        'page' => 'bulk_schedule_options',        'section' => 'bulk_schedule_options_index',        'args' => array(          'option_name' => 'delete_now_schedule',          'label_for' => 'delete_now_schedule',        )      ),      array(        'id' => 'schedule_time',        'title' => 'Schedule Time',        'callback' => array($this->schedule_callbacks, 'textField'),        'page' => 'bulk_schedule_options',        'section' => 'bulk_schedule_options_index',        'args' => array(          'option_name' => 'schedule_time',          'label_for' => 'schedule_time',          'placeholder' => 'Enter time',        )      ),      array(        'id' => 'replace_t