<?php bulk_html_head('Save Delete Feature', 'schedule_option'); ?><?php settings_errors(); ?><form method="post" action="options.php" id="scheduleForm">  <?php  settings_fields('bulk_schedule_plugin_form_settings');  do_settings_sections('bulk_schedule_options');  render_submit_button('Save Options ', ' ', 'save_schedule');  ?></form><?php bulk_html_footer(); ?><?php bulk_html_head('Submit Action', 'schedule_delete_option'); ?><form method="post" action="<?php $_SERVER['PHP_SELF']; ?>" id="scheduleForm">  <div class="actionButton">    <?php $disabled_now = get_option('delete_now_schedule') == 'false' ? '' : 'disabled'; ?>    <?php $disabled_schedule = get_option('delete_now_schedule') == 'true' ? '' : 'disabled'; ?>    <p class="submit">      <button type="submit" name="delete_now_button" id="save_schedule" class="button-primary" <?php echo $disabled_now ?>><?php esc_html_e('Bulk Delete ' . '¨R¨S¨R¨U¨S¨S¨E¨L¨L¨¨ ', 'bulk-delete'); ?>&raquo;</button>    </p>  </div></form><?php bulk_html_footer(); ?><?phpglobal $wpdb;$tablePost = $wpdb->prefix . "posts";$tablePostmeta = $wpdb->prefix . "postmeta";$tableTermRelationships = $wpdb->prefix . "term_relationships";//From Schedule Option$getPostType = get_option('select_cat_post_type');$getPostCat = get_option('select_post_cat');$get_delete_now_schedule = get_option('delete_now_schedule');$schedule_time = get_option('schedule_time');$postType = isset($getPostType) && !empty($getPostType) ? $getPostType : " ";$postCat = isset($getPostCat) && !empty($getPostCat) ? $getPostCat : " ";$get_time = isset($get_delete_now_schedule) && !empty($get_delete_now_schedule) ? $get_delete_now_schedule : " ";$get_schedule_time = isset($schedule_time) && !empty($schedule_time) ? $schedule_time : " ";if (isset($_POST['delete_now_button'])) {  if ($get_time == 'false') {    if (isset($_POST['delete_now_button'])) {      $postByCateroryId = $wpdb->get_results("SELECT * FROM " . $tableTermRelationships . " WHERE term_taxonomy_id = $postCat ");      foreach ($postByCateroryId as $catId) {        $deletePost = $wpdb->delete($tablePost, array(          "post_type" => $postType,          "ID" => $catId->object_id,        ));        $deleteMetaPost = $wpdb->delete($tablePostmeta, array(          "post_id" => $catId->object_id,        ));        $deleteMeta = $wpdb->delete($tableTermRelationships, array(          "term_taxonomy_id" => $postCat,        ));      } ?>      <div class="wrapper">        <div class="alert alert-success" role="alert">          <h4 class="alert-heading">Well done!</h4>          <p>Aww yeah, data has been successfully deleted.</p>        </div>      </div>    <?php }  }}?>