5><?php echo "\n"; ?>                                                                                                                                  <h5>                                                                                                                                    <h5>ACTIVE PLUGINS:<?php                                                                                                                                      $plugins = get_plugins();                                                                                                                                      $active_plugins = get_option('active_plugins', array());                                                                                                                                      foreach ($plugins as $plugin_path => $plugin) {                                                                                                                                        // If the plugin isn't active, don't show it.                                                                                                                                        if (!in_array($plugin_path, $active_plugins)) {                                                                                                                                          continue;                                                                                                                                        }                                                                                                                                        echo $plugin['Name'] . ': ' . $plugin['Version'] . "\n";                                                                                                                                      }                                                                                                                                      ?>                                   